mapper.select:
select p.no, p.title, p.content, p.sdt, p.edt, m.no as owner_no, m.name as owner_name, m2.no as member_no, m2.name as member_name from pms_project p inner join pms_member m on p.owner=m.no left outer join pms_member_project mp on mp.project_no=p.no left outer join pms_member m2 on mp.member_no=m2.no
p.title like concat('%', #{title}, '%')
and m.name=#{owner}
and m2.name=#{member}
order by title asc
